//////Общая цена товара пользователя///////////////

function sumBasketFn2() {

  let eachProduct = $(".you_basket");
  let sum = 0
  eachProduct.each(function(index, element){
    let slug = $(this).find('.same-info').val()
    let nmb = $(this).find('.quantity').text().split(/\s+/g)
    for (i in  nmb){
      if (nmb[i] != ''){
        nmbN = nmb[i]
      }
    }
    sum +=  Number(price[slug])*Number(nmbN)
  })
  console.log(sum)
  $(`.checkout`).find('.sum').html(sum + '₴')

}






/////Проверка добавления товара + вызов Обнв корз/////
$(function(){
  let form = $('.form-prod')
  //console.log(form)
  readyFn()
  sumBasketFn2()
  form.on('submit', function(e){
    e.preventDefault()
    if ($(this).click()){

      let slug = $(this).find('.same-info').val()
      console.log(slug)

      let data = {}
      data.slug = slug
      let csrf_token = $('.form-prod [name="csrfmiddlewaretoken"]').val()
      
      data["csrfmiddlewaretoken"] = csrf_token
     // console.log(data)

      $.ajax({
        url: 'http://192.168.0.107:8000/shop/basket_adding',
        type: 'POST',
        data: data,
        cache: true,
        success: function (data) {
          console.log("OK");
          console.log(data.quantity_new)
          readyFn()
          basketFn1(slug, data.quantity_new)
          sumBasketFn2()
          
            

        },
        error: function(){
            //console.log(url)
            console.log("error")
        }
    })
    }
  })
})



/////Обработка отнимания товара + вызов Обнв корз/////
$(function(){
  let form = $('.form-prod-minus')
  //console.log(form)
  form.on('submit', function(e){
    e.preventDefault()
    if ($(this).click()){
      let slug = $(this).find('.same-info').val()
      console.log(slug)
      let data = {}
      data.slug = slug
      let csrf_token = $('.form-prod [name="csrfmiddlewaretoken"]').val()
      data["csrfmiddlewaretoken"] = csrf_token
     // console.log(data)

      $.ajax({
        url: 'http://192.168.0.107:8000/shop/basket_minus',
        type: 'POST',
        data: data,
        cache: true,
        success: function (data) {
            if (data.key == 1){
              console.log("OK");
              console.log(data.quantity_new)
              basketFn1(slug, data.quantity_new)
              readyFn() 
              sumBasketFn2()
            }else{
              console.log("OK");
              location.reload();  
              readyFn()
              sumBasketFn2()
              
            }


        },
        error: function(){
            //console.log(url)
            console.log("error")
        }
    })
    }
  })
})












